var fn = msg.payload;
const nodeSettings =  msg.node_settings

let hasUpdate = false;
const updateNodes = [];
for(var i=0;i<fn.length;i++) {
    for(const nodeSetting of nodeSettings){
        if (fn[i].name == nodeSetting.node) {
            hasUpdate =true;
            if(fn[i].type== 'mqtt-broker'){
                fn[i].port = nodeSetting.port
                fn[i].broker =  nodeSetting.server
                fn[i].credentials =  {user: nodeSetting.username, password: nodeSetting.password}
            }else{
                for(const k of Object.keys(nodeSetting)){
                    if(k==='node'){
                        continue;
                    }
                    fn[i][k]=nodeSetting[k]
                }
            }
            updateNodes.push(fn[i])
            break;
        }
    }
}

msg.original_msg.payload = updateNodes

if(hasUpdate){

    return [
        msg.original_msg,
        {
            payload: msg.payload,
            headers : {
                'Content-Type': 'application/json',
                'Accept' : 'application/json',
                "Node-RED-Deployment-Type":"nodes",
                Authorization: 'Bearer '+msg.token,
            },
            url : "http://localhost:1880/admin/flows",
            method :"POST"
        }
    ]
}

