const rows = msg.payload.split("\n")
let foundEth0 = false;
let mac = ""
for(const row of rows){
    if(foundEth0){
        if(row.indexOf("link/ether")>=0){

            // link/ether b8:27:eb:93:40:69 brd ff:ff:ff:ff:ff:ff
            const rowSplit = row.trim().split(/\s+/)
            msg.rowSPlit = rowSplit
            mac = rowSplit[1];
            if(mac){
                mac = mac.replace(/:/g,'').toUpperCase()
            }
            break;
        }
    }else if(row.indexOf("eth0")>=0){
        foundEth0=true
    }
}
global.set("mac",mac,"settings")
return {
    payload:mac
}

if(!msg.payload.success){
    msg.original_msg.payload = msg.payload.error
    return [msg.original_msg,null]
}

const newSetting = msg.payload.settings
global.set("settings",newSetting,"settings")
msg.payload = newSetting

return [null,msg];

