const oldVer = global.get('flow_version')
const newVer = msg.payload.flow.version
if(oldVer==newVer){
    return [null,msg]
}
const oldParts = oldVer.split('.')
const newParts = newVer.split('.')
for (var i = 0; i < newParts.length; i++) {
    const a = ~~newParts[i] // parse int
    const b = ~~oldParts[i] // parse int
    if (a > b) return [msg,null]
    if (a < b) return [null,msg]
}

const totalTry = env.get("try");
if(totalTry > 0){
    if(msg.try_count >= totalTry){
        return [msg,null]
    }
}
return [null,msg]

