const direction = msg.payload
const settings = global.get('settings', 'settings')
let p_passenger = global.get('p_passenger') || 0
if(direction==='in'){
    if(p_passenger < (settings.max_passenger || 16)){
        p_passenger++
    }
    global.set("p_in", (global.get('p_in') || 0)+1)
}else if(direction==='out'){
    if(p_passenger > 0)
        p_passenger--
    global.set("p_out", (global.get('p_out') || 0)+1)
}
global.set("p_passenger", p_passenger)

return msg;

